mapper.select:
SELECT c.course_id, c.trainer_id, c.title, c.type, c.status, c.lesson_form, c.is_free, c.difficulty, c.schedule, c.main_image FROM training_course c WHERE c.trainer_id = #{userId} AND c.is_deleted = 0
AND c.status IN
#{status}
AND c.type IN
#{type}
AND c.lesson_form IN
#{lessonForm}
ORDER BY c.created_at DESC

select count(*) > 0
from training_course
where course_id = #{courseId}
  and trainer_id = #{userId}

select count(*) > 0
from training_course
where tags = #{tags}
